mapper.select:
select user_id, user_pw
from user
where user_id = #{userId}

mapper.insert:
insert into
user(
user_id, user_pw, user_name, user_nickname, profile_picture
) values (
#{userId}, #{userPw}, #{userName}, #{userNickname}, #{profilePicture}
)

mapper.update:
update user
set user_pw = #{userPw},
user_name = #{userName},
user_nickname = #{userNickname},
profile_picture = #{profilePicture}
where user_id = #{userId}
